import React, { useState, useEffect, useRef } from 'react';
import { AppBar, Toolbar, Typography, Button, Container, Card, CardContent, CardActions, Grid, TextField, useScrollTrigger, Slide, CssBaseline, IconButton } from '@mui/material';
import { Link } from 'react-router-dom';
import { CountdownCircleTimer } from 'react-countdown';
import heroImage from '../assets/hero-image.png';
import Group1 from '../assets/hero-image.png';
import { Icon } from '@iconify/react';
import ArrowUpwardIcon from '@mui/icons-material/ArrowUpward';
import Typed from "typed.js";



// CSS for the fixed navigation bar
const appBarStyles = {
    position: 'static',
    backgroundColor: 'white',
    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.2)',
};

// CSS for the scrolled navigation bar
const scrolledAppBarStyles = {
    backgroundColor: 'white',
    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.2)',
};

function HideOnScroll(props) {
    const { children, window } = props;
    const trigger = useScrollTrigger({
        target: window ? window() : undefined,
    });

    return (
        <Slide appear={false} direction="down" in={!trigger}>
            {children}
        </Slide>
    );
}

const HomePage = () => {

    const el = useRef(null);

    useEffect(() => {
        const typed = new Typed(el.current, {
            strings: ["Cards", "Friends", "Families", "Merchant", "Classes"],
            startDelay: 300,
            typeSpeed: 120,
            backSpeed: 80,
            backDelay: 1200,
            smartBackspace: true,
            loop: true,
            showCursor: false,
            cursorChar: "|",
        });

        return () => {
            typed.destroy();
        };
    }, []);

    const [scrolled, setScrolled] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            if (window.scrollY > 10) {
                setScrolled(true);
            } else {
                setScrolled(false);
            }
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);
    // Calculate the target date and time for the countdown
    const targetDate = new Date('2023-11-01T00:00:00'); // Set your target date and time

    // Calculate the difference in time between the current date and the target date
    const timeDifference = targetDate - new Date();

    // Calculate the remaining days, hours, minutes, and seconds
    const days = Math.floor(timeDifference / (1000 * 60 * 60 * 24));
    const hours = Math.floor((timeDifference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    const minutes = Math.floor((timeDifference % (1000 * 60 * 60)) / (1000 * 60));
    const seconds = Math.floor((timeDifference % (1000 * 60)) / 1000);

    return (
        <div>
            <CssBaseline />
            <HideOnScroll>
                <AppBar style={scrolled ? scrolledAppBarStyles : appBarStyles}>
                    <Toolbar>
                        <img src="logo.png" alt="MoneySync Pay" style={{ maxWidth: '120px' }} />
                        <div style={{ flex: 1 }}></div>
                        <Button component={Link} to="/">Home</Button>
                        <Button component={Link} to="/about">About</Button>
                        <Button component={Link} to="/features">Features</Button>
                    </Toolbar>
                </AppBar>
            </HideOnScroll>


            <Container>
                <section style={{ marginBottom: '20%' }}>
                    <div style={{ display: 'flex' }}>
                        <div style={{ flex: 1 }}>
                            <div className="text-center my-6 md:my-16" style={{ marginTop: '50px' }}>
                                <div className="relative">
                                    <h1 className="font-bold text-4xl md:text-6xl mb-4 md:mb-6 leading-[56px] z-50">
                                        All Your <span className="text-blue-600" ref={el}></span>
                                        <br />
                                        Payment Made Easy
                                    </h1>
                                </div>
                                <p className="font-light mb-4 md:mb-10 max-w-xs mx-auto opacity-90">
                                    The Tap n Pay App, Make payments easy and mindful
                                </p>
                                <h5 className="font-semibold bg-blue-600 max-w-max mx-auto px-4 md:px-12 py-2 md:py-4 rounded-lg text-white cursor-pointer hover-bg-blue-700">
                                    See Our Progress
                                </h5>
                            </div>
                        </div>
                        <div style={{ flex: 1 }}>
                            <img src={heroImage} alt="Placeholder" style={{ width: '100%', animation: 'moveRight 2s linear' }} />
                        </div>
                    </div>
                </section>

                <section style={{ marginBottom: '20%' }}>
                    <div className="mx-4 sm:mx-20 my-10 sm:my-40 flex flex-wrap justify-between items-center">
                        <div className="w-full sm:w-2/3 space-y-6">
                            <h2 className="font-semibold text-3xl sm:text-5xl my-4">Our Features</h2>

                            <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
                                {/* Feature 1 */}
                                <div className="border-[1.6px] p-4 rounded-md flex space-x-4">
                                    <div className="bg-blue-200 rounded-full p-2 flex justify-center items-center h-16 w-16">
                                        <Icon icon="mdi:shop-outline" className="text-blue-600 h-10 w-10" />
                                    </div>
                                    <div>
                                        <h5 className="font-semibold text-lg">Instant Merchant Transfer</h5>
                                        <p className="text-gray-600">
                                            No waiting time to receive the money; just tap, and the money will be in your account.
                                        </p>
                                    </div>
                                </div>

                                {/* Feature 2 */}
                                <div className="border-[1.6px] p-4 rounded-md flex space-x-4">
                                    <div className="bg-blue-200 rounded-full p-2 flex justify-center items-center h-16 w-16">
                                        <Icon icon="ic:round-add-card" className="text-blue-600 h-10 w-10" />
                                    </div>
                                    <div>
                                        <h5 className="font-semibold text-lg">Card Management and Analysis</h5>
                                        <p className="text-gray-600">
                                            Provide detailed analysis of your card payments and history.
                                        </p>
                                    </div>
                                </div>
                            </div>

                            <div className="grid grid-cols-1 sm:grid-cols-2 gap-6 mt-6">
                                {/* Feature 3 */}
                                <div className="border-[1.6px] p-4 rounded-md flex space-x-4">
                                    <div className="bg-blue-200 rounded-full p-2 flex justify-center items-center h-16 w-16">
                                        <Icon icon="ri:p2p-fill" className="text-blue-600 h-10 w-10" />
                                    </div>
                                    <div>
                                        <h5 className="font-semibold text-lg">Peer to Peer</h5>
                                        <p className="text-gray-600">
                                            To boost our user experience, we make it more interactive and fun.
                                        </p>
                                    </div>
                                </div>

                                {/* Feature 4 */}
                                <div className="border-[1.6px] p-4 rounded-md flex space-x-4">
                                    <div className="bg-blue-200 rounded-full p-2 flex justify-center items-center h-16 w-16">
                                        <Icon icon="material-symbols:tap-and-play" className="text-blue-600 h-10 w-10" />
                                    </div>
                                    <div>
                                        <h5 className="font-semibold text-lg">Tap and Pay</h5>
                                        <p className="text-gray-600">
                                            Just tap with biometric authentication; the payment will be completed.
                                        </p>
                                    </div>
                                </div>
                            </div>

                            <div className="grid grid-cols-1 sm:grid-cols-2 gap-6 mt-6">
                                {/* Feature 5 */}
                                <div className="border-[1.6px] p-4 rounded-md flex space-x-4">
                                    <div className="bg-blue-200 rounded-full p-2 flex justify-center items-center h-16 w-16">
                                        <Icon icon="mdi:shop-outline" className="text-blue-600 h-10 w-10" />
                                    </div>
                                    <div>
                                        <h5 className="font-semibold text-lg">Offline Transactions</h5>
                                        <p className="text-gray-600">
                                            We provide tap n pay for PoS even if there is no internet.
                                        </p>
                                    </div>
                                </div>

                                {/* Feature 6 */}
                                <div className="border-[1.6px] p-4 rounded-md flex space-x-4">
                                    <div className="bg-blue-200 rounded-full p-2 flex justify-center items-center h-16 w-16">
                                        <Icon icon="ant-design:stock-outlined" className="text-blue-600 h-10 w-10" />
                                    </div>
                                    <div>
                                        <h5 className="font-semibold text-lg">RFA AI</h5>
                                        <p className="text-gray-600">
                                            Risk Factor Analysis AI in-house API for your investment recommendations.
                                        </p>
                                    </div>
                                </div>
                            </div>

                            <p className="text-lg font-medium text-blue-600 mt-6">And many more!</p>
                        </div>

                        <div className="w-full sm:w-1/3 bg-blue-200 rounded-xl px-4 sm:px-20 py-0 mt-6">
                            <img src={Group1} alt="" className="w-full scale-100 pb-10" />
                        </div>
                    </div>
                </section>

                <section style={{ marginBottom: '20%' }}>
                    <h2>Coming Soon</h2>
                    <p>Please register for beta!</p>
                    <TextField label="Enter your email" variant="outlined" fullWidth />
                    <Button variant="contained" color="primary">Register</Button>
                </section>

                <section style={{ marginBottom: '20%' }}>
                    <h2>Countdown</h2>
                    <Typography variant="h2">
                        {`${days}:${hours}:${minutes}:${seconds}`}
                    </Typography>
                </section>
            </Container>

            <footer>
                <div className="border-t-2 border-gray-200 mt-16 sm:mt-32">
                    <div className="mx-4 sm:mx-20 p-6 space-y-8 sm:space-y-12">
                        <div className="flex flex-col sm:flex-row items-center sm:items-start sm:space-x-16">
                            <div className="flex items-center text-center sm:text-left">
                                <img src={Group1} alt="" style={{ width: "100px", height: "100px" }} />
                                <div className="ml-4">
                                    <a
                                        href="https://www.linkedin.com/company/money-sync-pay/?viewAsMember=true"
                                        target="_blank"
                                        rel="noopener noreferrer"
                                    >
                                        <button className="bg-blue-200 text-blue-600 p-2 rounded-full mt-2 sm:mt-4 hover:bg-blue-600 hover:text-white transition-all duration-200 ease-out">
                                            <Icon icon="line-md:linkedin" className="h-6 w-6 sm:h-10 w-10" />
                                        </button>
                                    </a>
                                </div>
                            </div>
                            <div className="sm:w-full">
                                <div className="flex flex-col space-y-2 mt-4 sm:mt-0 sm:items-start">
                                    <h5 className="font-medium text-lg">Pages</h5>
                                    <p className="font-light">Home</p>
                                    <p className="font-light">About Us</p>
                                </div>
                                <div className="flex flex-col space-y-2 mt-4 sm:mt-0 sm:items-start">
                                    <h5 className="font-medium text-lg">Our Progress</h5>
                                    <a
                                        href="https://www.linkedin.com/company/money-sync-pay/?viewAsMember=true"
                                        target="_blank"
                                        rel="noopener noreferrer"
                                    >
                                        <p className="font-light">LinkedIn</p>
                                    </a>
                                </div>
                            </div>
                        </div>

                        <h5 className="text-center font-medium mt-8">
                            Copyright &copy; 2022 <span className="text-blue-600">Money Sync</span>.
                            All rights reserved.
                        </h5>
                    </div>
                </div>
            </footer>
        </div>
    );
};

export default HomePage;
