import React, { useState, useEffect } from 'react';
import { AppBar, Toolbar, Button, useScrollTrigger, Slide, CssBaseline } from '@mui/material';
import { Link } from 'react-router-dom';
import logo from '../assets/logo.svg';
import { Icon } from '@iconify/react';
import olderImage from '../assets/pay-by-bank-card.svg';


// CSS for the fixed navigation bar
const appBarStyles = {
    position: 'static',
    backgroundColor: 'white',
    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.2)',
};

// CSS for the scrolled navigation bar
const scrolledAppBarStyles = {
    backgroundColor: 'white',
    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.2)',
};

function HideOnScroll(props) {
    const { children, window } = props;
    const trigger = useScrollTrigger({
        target: window ? window() : undefined,
    });

    return (
        <Slide appear={false} direction="down" in={!trigger}>
            {children}
        </Slide>
    );
}

const FeatureCard = ({ feature }) => {
    const [expanded, setExpanded] = useState(false);

    // Define an animation for expanding and collapsing the content
    const contentProps = useSpring({
        height: expanded ? 'auto' : 0,
        opacity: expanded ? 1 : 0,
    });

    return (
        <div className="bg-white shadow-lg rounded-lg mb-4 overflow-hidden">
            <div
                className="p-4 cursor-pointer"
                onClick={() => setExpanded(!expanded)}
            >
                <h2 className="text-xl font-semibold mb-2">{feature.heading}</h2>
                <p className="text-gray-600">
                    {feature.description.substring(0, expanded ? feature.description.length : 100) +
                        (expanded ? '' : '...')}
                </p>
            </div>
            <animated.div style={contentProps} className="p-4">
                <p className="text-gray-600">{feature.description}</p>
            </animated.div>
        </div>
    );
};



const Features = () => {

    const features = [
        {
            heading: 'Instant Merchant Transfer',
            description:
                'No waiting time to receive the money; just tap, and the money will be in your account.',
            image: olderImage,
        },
        {
            heading: 'Card Management and Analysis',
            description: 'Provide detailed analysis of your card payments and history.',
            image: olderImage,
        },
        {
            heading: 'Peer to Peer',
            description: 'To boost our user experience, we make it more interactive and fun.',
            image: olderImage,
        },
        {
            heading: 'Tap and Pay',
            description: 'Just tap with biometric authentication; the payment will be completed.',
            image: olderImage,
        },
        {
            heading: 'Offline Transactions',
            description: 'We provide tap n pay for PoS even if there is no internet.',
            image: olderImage,
        },
        {
            heading: 'RFA AI',
            description: 'Risk Factor Analysis AI in-house API for your investment recommendations.',
            image: olderImage,
        },
    ];

    const [scrolled, setScrolled] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            if (window.scrollY > 10) {
                setScrolled(true);
            } else {
                setScrolled(false);
            }
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);
    // Calculate the target date and time for the countdown

    return (
        <div>
            <CssBaseline />
            <HideOnScroll>
                <AppBar style={scrolled ? scrolledAppBarStyles : appBarStyles}>
                    <Toolbar>
                        <img src={logo} alt="MoneySync Pay" style={{ maxWidth: '120px' }} />
                        <div style={{ flex: 1 }}></div>
                        <Button component={Link} to="/">Home</Button>
                        <Button component={Link} to="/about">About</Button>
                        <Button component={Link} to="/features">Features</Button>
                    </Toolbar>
                </AppBar>
            </HideOnScroll>
            <div className="container mx-auto grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                {features.map((feature, index) => (
                    <FeatureCard key={index} feature={feature} />
                ))}
            </div>
            {/* <div className="w-full sm:mx-50 my-10 sm:my-40">
                {features.map((feature, index) => (
                    <div key={index} className="flex flex-wrap items-center justify-center space-x-10">
                        {index % 2 === 0 ? (
                            <>
                                <div className="bg-white rounded-lg shadow-md p-6 h-100 w-600">
                                    <div className="w-full sm:w-1/2 space-y-6 sm:space-y-10">
                                        <h2 className="font-semibold text-3xl sm:text-5xl my-4">{feature.heading}</h2>
                                        <p className="text-gray-600">{feature.description}</p>
                                    </div>
                                </div>
                                <div className="p-2 flex justify-center items-center h-100 w-100">
                                    <img src={feature.image} alt={feature.heading} width="300px" />
                                </div>
                            </>
                        ) : (
                            <>
                                <div className="p-2 flex justify-center items-center h-100 w-100">
                                    <img src={feature.image} alt={feature.heading} width="300px" />
