import React, { useState, useEffect, useRef } from 'react';
import { AppBar, Toolbar, Typography, Button, Container, Card, CardContent, CardActions, Grid, TextField, useScrollTrigger, Slide, CssBaseline, IconButton } from '@mui/material';
import { Link } from 'react-router-dom';
import { CountdownCircleTimer } from 'react-countdown';
import heroImage from '../assets/hero-image.png';
import Group1 from '../assets/hero-image.png';
import logo from '../assets/logo.svg';
import { Icon } from '@iconify/react';
import {
    CreditCardIcon,
    StatusOfflineIcon,
    WifiIcon
} from "@heroicons/react/outline";
import ArrowUpwardIcon from '@mui/icons-material/ArrowUpward';
import Typed from "typed.js";
import CountdownTimer from './CountdownTimer';
import AboutPage from '../pages/About';



// CSS for the fixed navigation bar
const appBarStyles = {
    position: 'static',
    backgroundColor: 'white',
    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.2)',
};

// CSS for the scrolled navigation bar
const scrolledAppBarStyles = {
    backgroundColor: 'white',
    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.2)',
};

function HideOnScroll(props) {
    const { children, window } = props;
    const trigger = useScrollTrigger({
        target: window ? window() : undefined,
    });

    return (
        <Slide appear={false} direction="down" in={!trigger}>
            {children}
        </Slide>
    );
}
const cardStyle = {
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)", // Adjust the shadow properties as needed
    borderRadius: "8px", // Add rounded corners for a card-like appearance
};

function FeatureCard({ icon, title, description }) {
    const [isHovered, setIsHovered] = useState(false);

    return (
        <div className={`card ${isHovered ? 'hovered' : ''}`} onMouseEnter={() => setIsHovered(true)} onMouseLeave={() => setIsHovered(false)}>
            <div className={`content ${isHovered ? 'hovered' : ''}`}>
                <div className="icon">{icon}</div>
                <h5 className="title">{title}</h5>
                <p className="description">{description}</p>
            </div>
        </div>
    );
}



const AboutUs = () => {
    const [isViewMoreHovered, setIsViewMoreHovered] = useState(false);

    const el = useRef(null);

    useEffect(() => {
        const typed = new Typed(el.current, {
            strings: ["Cards", "Friends", "Families", "Merchant", "Classes"],
            startDelay: 300,
            typeSpeed: 120,
            backSpeed: 80,
            backDelay: 1200,
            smartBackspace: true,
            loop: true,
            showCursor: false,
            cursorChar: "|",
        });

        return () => {
            typed.destroy();
        };
    }, []);

    const [scrolled, setScrolled] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            if (window.scrollY > 10) {
                setScrolled(true);
            } else {
                setScrolled(false);
            }
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);
    // Calculate the target date and time for the countdown

    return (
        <div>
            <CssBaseline />
            <HideOnScroll>
                <AppBar style={scrolled ? scrolledAppBarStyles : appBarStyles}>
                    <Toolbar>
                        <img src={logo} alt="MoneySync Pay" style={{ maxWidth: '120px' }} />
                        <div style={{ flex: 1 }}></div>
                        <Button component={Link} to="/">Home</Button>
                        <Button component={Link} to="/about">About</Button>
                        <Button component={Link} to="/features">Features</Button>
                    </Toolbar>
                </AppBar>
            </HideOnScroll>

            <div className="text-center mx-20 my-12">
                <h1 className="text-6xl font-semibold my-4">About Us</h1>
                <p className="text-light opacity-80 mx-auto">
                    A group of like mind coming together
                    <br />
                    However the development of Money Sync is held by 2 People.
                </p>
                <div className="flex mt-12 justify-between w-2/5 mx-auto">

                    <div className="flex flex-col items-center border-gray-100 rounded-md border-2 max-w-max py-5 px-10 transition-all duration-150 ease-out hover:scale-105">

                        <h5 className="font-semibold mt-5">Xexon</h5>
                        <p className="font-light">Founding Technical Member</p>
                        <div className="flex mt-8 gap-6 w-max">
                            <Icon
                                icon="akar-icons:github-fill"
                                className="h-7 w-7 text-blue-600 cursor-pointer hover:text-blue-700"
                            />
                            <Icon
                                icon="akar-icons:instagram-fill"
                                className="h-7 w-7 text-blue-600 cursor-pointer hover:text-blue-700"
                            />
                            <Icon
                                icon="akar-icons:twitter-fill"
                                className="h-7 w-7 text-blue-600 cursor-pointer hover:text-blue-700"
                            />
                        </div>
