import React, { useState } from 'react';
import { useSpring, animated } from 'react-spring';
import '../FeatureCard.css'; // Import your CSS file

const FeatureCard = ({ feature }) => {
    const [hovered, setHovered] = useState(false);

    const cardAnimation = useSpring({
      transform: hovered ? 'scale(1.05)' : 'scale(1)',
      boxShadow: hovered ? '0px 6px 10px rgba(0, 0, 0, 0.2)' : '0px 4px 6px rgba(0, 0, 0, 0.1)',
    });

    return (
      <animated.div
        className="feature-card"
        onMouseEnter={() => setHovered(true)}
        onMouseLeave={() => setHovered(false)}
        style={cardAnimation}
      >
        <h2 className="text-xl font-semibold mb-2">{feature.heading}</h2>
        <img src={feature.image} alt={feature.heading} className="feature-image" />
        {hovered && (
          <animated.p className="feature-description">
            {feature.description}
          </animated.p>
        )}
      </animated.div>
    );
  };

  export default FeatureCard;
