import React, { useState, useEffect, useRef } from "react";

function Countdown() {
  const refInstance = useRef(null);
  const [counter, setCountdown] = useState("00:00:00:00");

  const getCounter = (e) => {
    const all = Date.parse(e) - Date.parse(new Date());
    const s = Math.floor((all / 1000) % 60);
    const m = Math.floor((all / 1000 / 60) % 60);
    const h = Math.floor((all / 1000 / 60 / 60) % 24);
    const d = Math.floor(all / 1000 / 60 / 60 / 24);

    return {
      all,
      s,
      m,
      h,
      d,
    };
  };

  const initCounter = (e) => {
    const { all, d, h, m, s } = getCounter(e);
    if (all >= 0) {
      setCountdown(
        `${d.toString().padStart(2, "0")}:${h.toString().padStart(
          2,
          "0"
        )}:${m.toString().padStart(2, "0")}:${s.toString().padStart(2, "0")}`
      );
    }
  };

  const reset = (e) => {
    setCountdown("01:00:00:20");
    if (refInstance.current) clearInterval(refInstance.current);
    const id = setInterval(() => {
      initCounter(e);
    }, 1000);
    refInstance.current = id;
  };

  const voidTime = () => {
    const voidZone = new Date();
    voidZone.setSeconds(voidZone.getSeconds() + 20);
    return voidZone;
  };

  useEffect(() => {
    reset(voidTime());
  }, []);

  return (
    <>
      <h1 className="text-4xl font-semibold justify-center">{counter}</h1>
    </>
  );
}

export default Countdown;
